INSERT INTO `sys_oper_log` VALUES (298, 'course', 2, 'site.hgu7.core.course.controller.CourseController.edit()', 'PUT', 1, 'admin', '研发部门', '/course/course', '222.223.243.26', 'XX XX', '{\"courseCategory\":\"00\",\"courseDescription\":\"线性代数是一门重要的数学基础课程，研究变量间的线性关系。它在自然科学、社会科学、工程技术、军事和工农业生产等领域中有广泛的应用。课程内容包括行列式、矩阵、向量的线性相关性、线性方程组、相似矩阵和二次型等理论。学习该课程需要具备微积分和代数基本知识。\",\"courseId\":1,\"courseImg\":\"https://tse4-mm.cn.bing.net/th/id/OIP-C.fK7K5Lvtke9pBIs-x0hN_wHaEY?w=290&h=180&c=7&r=0&o=5&pid=1.7\",\"courseName\":\"线性代数\",\"createdAt\":\"2024-10-30\",\"params\":{},\"teacher\":\"宋浩\",\"updatedAt\":\"2024-10-30\",\"userId\":1}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-11-04 14:31:53', 658);
INSERT INTO `sys_oper_log` VALUES (299, 'course', 2, 'site.hgu7.core.course.controller.CourseController.edit()', 'PUT', 1, 'admin', '研发部门', '/course/course', '222.223.243.26', 'XX XX', '{\"courseCategory\":\"00\",\"courseDescription\":\"高等数学是一门十分重要的基础理论课。它的主要研究对象为实变实值函数，尤其是连续的实变实值函数。本课程包括的主要内容有：一元函数的极限，连续、微分、积分，级数及多元函数的极限、连续、微分、积分（含参积分，线积分、重积分、面积分）、空间解析几何、微分方程等。\",\"courseId\":2,\"courseImg\":\"https://ts1.cn.mm.bing.net/th?id=OVP.O5FgFLeZI404cxGQkyzhywIIFE&w=608&h=342&c=7&rs=1&qlt=90&o=6&pid=1.7\",\"courseName\":\"高等数学\",\"createdAt\":\"2024-10-28\",\"params\":{},\"teacher\":\"宋浩\",\"updatedAt\":\"2024-10-28\",\"userId\":1}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-11-04 14:33:26', 25);
INSERT INTO `sys_oper_log` VALUES (300, 'course', 2, 'site.hgu7.core.course.controller.CourseController.edit()', 'PUT', 1, 'admin', '研发部门', '/course/course', '222.223.243.26', 'XX XX', '{\"courseCategory\":\"80\",\"courseDescription\":\"《计算机网络基础》是网络工程专业学生的必修课程，是一门集计算机技术与通信技术、 软件与硬件、理论与实践为一体的课程。作为一门学科与专业基础性课程，本课程的学习将 为学生后续从事计算机网络应用、设计与开发和进一步的专业学习打下必要的基础。\",\"courseId\":3,\"courseImg\":\"https://ts1.cn.mm.bing.net/th?id=OVP.O5FgFLeZI404cxGQkyzhywIIFE&w=608&h=342&c=7&rs=1&qlt=90&o=6&pid=1.7\",\"courseName\":\"计算机网络\",\"createdAt\":\"2024-10-29\",\"params\":{},\"teacher\":\"湖科大教书匠\",\"updatedAt\":\"2024-10-31\",\"userId\":1}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-11-04 14:34:05', 33);
INSERT INTO `sys_oper_log` VALUES (301, 'course', 1, 'site.hgu7.core.course.controller.CourseController.add()', 'POST', 1, 'admin', '研发部门', '/course/course', '222.223.243.26', 'XX XX', '{\"courseCategory\":\"80\",\"courseDescription\":\"该课程讲授计算机单机系统的组成 原理，讨论计算机组织与结构，即在硬件与软件功能分配中最基本的硬件系统。 课程内容包括计算机主要部件类型、结构，组成和基本工作原理，讨论机内数据 流、数据通路和控制方式等，使学生在硬件层面理解计算机系统，为后续的操作 系统、编译原理等课程打下良好的基础。\",\"courseId\":5,\"courseName\":\"计算机组成原理\",\"createdAt\":\"2024-11-04\",\"params\":{},\"teacher\":\"王道\",\"updatedAt\":\"2024-11-04\",\"userId\":1}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-11-04 14:34:55', 319);
INSERT INTO `sys_oper_log` VALUES (302, 'course', 1, 'site.hgu7.core.course.controller.CourseController.add()', 'POST', 1, 'admin', '研发部门', '/course/course', '222.223.243.26', 'XX XX', '{\"courseCategory\":\"80\",\"courseDescription\":\"本课程是计算机类专业的必修课程，旨在全面系统地介绍操作系统的体系结构、设计机理及实现方法和技术，包括自启动装入、系统调用与接口、处理器调度及进/线程控制、同步与通信机制、死锁处理、基于分区/分页/分段的内存管理及虚拟存储、设备管理、文件系统等，从而培养同学在操作系统研发方面的理论基础及技术素养。\",\"courseId\":6,\"courseName\":\"操作系统\",\"createdAt\":\"2024-11-04\",\"params\":{},\"teacher\":\"王道\",\"updatedAt\":\"2024-11-04\",\"userId\":1}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-11-04 14:35:32', 10);
