INSERT INTO `sys_oper_log` VALUES (308, 'course_chapters', 2, 'site.hgu7.core.course.controller.CourseChaptersController.edit()', 'PUT', 1, 'admin', '研发部门', '/course/chapters', '222.223.243.26', 'XX XX', '{\"chapterDescription\":\"微积分的创立背景是在十六十七世纪，由于天文学、航海和力学的发展，提出了两大类科学问题，第一类问题包括光调曲线的切线和)法线、的速度和加速度以及如何求炮弹的最大射程，最终形成了做分学理论。第二类问题包括曲线的长度、平面图形的面积、空间物体的体积以及天体间的引力，最终形成了积分学理论。牛顿是经验的、具体的和谨慎的，而莱布尼茨则是富于想象的、喜欢推广的和大胆的。\",\"chapterId\":1,\"chapterTitle\":\"1.1 函数的几种特性\",\"courseId\":2,\"createdAt\":\"2024-10-31\",\"params\":{},\"position\":\"1\",\"updatedAt\":\"2024-10-31\",\"videoUrl\":\"http://s-bj-11294-hgu-zxxx.oss.dogecdn.com/gs1.1%20%E5%87%BD%E6%95%B0%E7%9A%84%E5%87%A0%E7%A7%8D%E7%89%B9%E6%80%A7.mp4\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-11-04 14:40:37', 94);
INSERT INTO `sys_oper_log` VALUES (309, 'course_chapters', 2, 'site.hgu7.core.course.controller.CourseChaptersController.edit()', 'PUT', 1, 'admin', '研发部门', '/course/chapters', '222.223.243.26', 'XX XX', '{\"chapterDescription\":\"本视频介绍了函数的概念与性质。函数起源于描述一个量依赖于另外一个量的变化，简要的函数例子、函数的运算以及函数的几个简单特性\",\"chapterId\":2,\"chapterTitle\":\"1.2 反函数 复合函数 初等函数\",\"courseId\":2,\"createdAt\":\"2024-10-31\",\"params\":{},\"position\":\"2\",\"updatedAt\":\"2024-10-31\",\"videoUrl\":\"http://s-bj-11294-hgu-zxxx.oss.dogecdn.com/gs1.1%20%E5%8F%8D%E5%87%BD%E6%95%B0%20%E5%A4%8D%E5%90%88%E5%87%BD%E6%95%B0%20%E5%88%9D%E7%AD%89%E5%87%BD%E6%95%B0.mp4\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-11-04 14:40:57', 14);
INSERT INTO `sys_oper_log` VALUES (310, 'course_chapters', 2, 'site.hgu7.core.course.controller.CourseChaptersController.edit()', 'PUT', 1, 'admin', '研发部门', '/course/chapters', '222.223.243.26', 'XX XX', '{\"chapterDescription\":\"本视频介绍了指数函数和对数函数的概念及其图形表示。指数函数是通过将一些点进行无限加密得到的连续曲线，而对数函数则是指数函数的反函数。对数西数表示了物质财盲和精神财富之间的关系，\\n即物质财富增加的量要更\\n多才能获得与精神财富增加相同的量。通过图形可以看出，对数函数的增量要比指数函数的增量大。本节课还介绍了基本初等函数和双曲函教。\",\"chapterId\":3,\"chapterTitle\":\"1.3 收敛数列的性质\",\"courseId\":2,\"createdAt\":\"2024-10-31\",\"params\":{},\"position\":\"3\",\"updatedAt\":\"2024-10-31\",\"videoUrl\":\"http://s-bj-11294-hgu-zxxx.oss.dogecdn.com/gs1.2%20%E6%94%B6%E6%95%9B%E6%95%B0%E5%88%97%E7%9A%84%E6%80%A7%E8%B4%A8.mp4\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2024-11-04 14:41:20', 13);
